DROP TABLE IF EXISTS `garment_type`;
DROP TABLE IF EXISTS `pattern_collection`;
DROP TABLE IF EXISTS `pattern_company`;
DROP TABLE IF EXISTS `pattern_for`;
DROP TABLE IF EXISTS `patterns`;
DROP TABLE IF EXISTS `patterns_garment_type`;
DROP TABLE IF EXISTS `patterns_pattern_for`;


CREATE TABLE `garment_type` (
  `id` int(11) NOT NULL DEFAULT '0',
  `name` varchar(128) DEFAULT NULL,
  `slug` varchar(128) DEFAULT NULL,
  `parent-id` varchar(128) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `pattern_collection` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) NOT NULL DEFAULT '',
  `slug` varchar(128) DEFAULT NULL,
  `pattern_company_id` int(11) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `pattern_company_id` (`pattern_company_id`),
  CONSTRAINT `pattern_collection_ibfk_1` FOREIGN KEY (`pattern_company_id`) REFERENCES `pattern_company` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8;


CREATE TABLE `pattern_company` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) DEFAULT NULL,
  `slug` varchar(128) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;


CREATE TABLE `pattern_for` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(128) DEFAULT NULL,
  `slug` varchar(128) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;


CREATE TABLE `patterns` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pattern_company_id` int(11) DEFAULT '0',
  `pattern_number` varchar(128) DEFAULT NULL,
  `pattern_collection_id` int(11) DEFAULT NULL,
  `url` varchar(500) DEFAULT NULL,
  `main_image` varchar(500) DEFAULT NULL,
  `line_drawing` varchar(500) DEFAULT NULL,
  `description` varchar(2000) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `pattern_company_id` (`pattern_company_id`),
  KEY `pattern_collection_id` (`pattern_collection_id`),
  CONSTRAINT `patterns_ibfk_1` FOREIGN KEY (`pattern_company_id`) REFERENCES `pattern_company` (`id`),
  CONSTRAINT `patterns_ibfk_2` FOREIGN KEY (`pattern_collection_id`) REFERENCES `pattern_collection` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=377 DEFAULT CHARSET=utf8;


CREATE TABLE `patterns_garment_type` (
  `pattern_id` int(11) DEFAULT NULL,
  `garment_type_id` int(128) DEFAULT '0',
  KEY `pattern_id` (`pattern_id`),
  KEY `garment_type_id` (`garment_type_id`),
  CONSTRAINT `patterns_garment_type_ibfk_2` FOREIGN KEY (`garment_type_id`) REFERENCES `garment_type` (`id`),
  CONSTRAINT `patterns_garment_type_ibfk_1` FOREIGN KEY (`pattern_id`) REFERENCES `patterns` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `patterns_pattern_for` (
  `pattern_id` int(11) DEFAULT NULL,
  `pattern_for_id` int(11) DEFAULT NULL,
  KEY `pattern_id` (`pattern_id`),
  KEY `pattern_for_id` (`pattern_for_id`),
  CONSTRAINT `patterns_pattern_for_ibfk_2` FOREIGN KEY (`pattern_for_id`) REFERENCES `pattern_for` (`id`),
  CONSTRAINT `patterns_pattern_for_ibfk_1` FOREIGN KEY (`pattern_id`) REFERENCES `patterns` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




